import React, { useState } from 'react';
import { createRoot } from 'react-dom';

function App() {
  return (
    <>
      <Tasks title="Дискриминант" />
      <Tasks title="высшая математика" />
      <Tasks title="Информатика за 9 класс" />
      <Tasks title="Физрук лох" />
      <Tasks title="Ядерная физика" />
      <Tasks title="Русский язык для умных" />
    </>
  );
}

const Tasks = ({ title }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="dropdown-menu">
      <button onClick={toggleMenu}>Toggle {title} Menu</button>
      {isOpen && (
        <ul>
          <li className="tasks">{title} - Option 1</li>
          <li className="tasks">{title} - Option 2</li>
          <li className="tasks">{title} - Option 3</li>
        </ul>
      )}
    </div>
  );
};

export default App;

const root = document.getElementById('root');
const rootElement = createRoot(root);
rootElement.render(<App />);
